import { fetchData } from "../../api/index";
import Axios from 'axios';
export default {
  name: 'basetable',
  data: function data() {
    return {
      query: {
        data: {},
        pageIndex: 1,
        pageSize: 10
      },
      tableData: [],
      multipleSelection: [],
      delList: [],
      pageTotal: 0,
      form: {},
      idx: -1,
      id: -1,
      options: []
    };
  },
  created: function created() {
    this.getData();
  },
  methods: {
    getData: function getData() {
      var _this = this;

      Axios.post("/api/article/findpage", this.query).then(function (response) {
        if (response.data.flag) {
          _this.$message.success(response.data.message);

          _this.tableData = response.data.data.rows;
          _this.pageTotal = response.data.data.total;
        } else {
          _this.$message.error(response.data.message);
        }
      });
    },
    // 触发搜索按钮
    handleSearch: function handleSearch() {
      var _this2 = this;

      this.query.pageIndex = 1;
      Axios.post("/api/article/findpage", this.query).then(function (response) {
        if (response.data.flag) {
          _this2.$message.success(response.data.message);

          _this2.tableData = response.data.data.rows;
          _this2.pageTotal = response.data.data.total;
        } else {
          _this2.$message.error(response.data.message);
        }
      });
    },
    // 删除操作
    handleDelete: function handleDelete(index, row) {
      var _this3 = this;

      // 二次确认删除
      this.$confirm('确定要删除吗？', '提示', {
        type: 'warning'
      }).then(function () {
        _this3.$message.success('删除成功');

        _this3.tableData.splice(index, 1);
      }).catch(function () {});
    },
    // 多选操作
    handleSelectionChange: function handleSelectionChange(val) {
      this.multipleSelection = val;
    },
    delAllSelection: function delAllSelection() {
      var length = this.multipleSelection.length;
      var str = '';
      this.delList = this.delList.concat(this.multipleSelection);

      for (var i = 0; i < length; i++) {
        str += this.multipleSelection[i].name + ' ';
      }

      this.$message.error("\u5220\u9664\u4E86".concat(str));
      this.multipleSelection = [];
    },
    // 编辑操作
    handleEdit: function handleEdit(index, row) {
      localStorage.setItem('article_id', row.id);
      this.$router.push('/article_edit');
    },
    // 分页导航
    handlePageChange: function handlePageChange(val) {
      this.$set(this.query, 'pageIndex', val);
      this.getData();
    },
    // 添加操作
    handleAdd: function handleAdd() {
      var _this4 = this;

      this.form = {};
      this.addVisible = true; //查询父分类树

      Axios.get("/api/category/tree").then(function (response) {
        _this4.options = response.data;
      });
    },
    //选择父类
    handleChange: function handleChange(value) {
      this.form.parentid = value[value.length - 1];
    },
    // 保存添加
    saveAdd: function saveAdd() {
      var _this5 = this;

      Axios.post("/api/category", this.form).then(function (response) {
        if (response.data.flag) {
          _this5.$message.success(response.data.message);
        } else {
          _this5.$message.error(response.data.message);
        }
      });
      this.addVisible = false;
      this.getData();
    }
  }
};
